import right from '../img/slider-right.svg'
import left from '../img/slider-left.svg'
import portfolio0 from '../img/portfolio0.png'

function Slider() {
    return (
        <div className="slider">
            <h2>Мое портфолио</h2>
            <div className="slider-container">
                <div className="slider-container__buttons">
                    <img className="slider-container__buttons-item" id="left" src={left} alt=""/>
                    <img className="slider-container__buttons-item" id="right" src={right} alt=""/>
                </div>
                <img className="slide-prev" src={portfolio0} alt=""/>
                <img className="slide-active" src={portfolio0} alt=""/>
                <img className="slide-next" src={portfolio0} alt=""/>
                <img className="slide-hidden__prev" src={portfolio0} alt=""/>
                <img className="slide-hidden__next" src={portfolio0} alt=""/>
                <img className="slide-hidden" src={portfolio0} alt=""/>
            </div>
        </div>
    )
}

document.addEventListener("DOMContentLoaded", function (event) {
    let prev = document.querySelector('#left')
    let next = document.querySelector('#right')

    let slidePrev = document.querySelector('.slide-prev')
    let slideActive = document.querySelector('.slide-active')
    let slideNext = document.querySelector('.slide-next')
    let slideHiddenNext = document.querySelector('.slide-hidden__next')
    let slideHidden = document.querySelector('.slide-hidden')
    let slideHiddenPrev = document.querySelector('.slide-hidden__prev')


    prev.onclick = function(){
        slideActive.style.transform = 'scale(0.7)'
        slideActive.style.filter = 'filter: brightness(0.2);'
    }

    next.onclick = function(){

    }




},false);




export default Slider
